import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Project } from 'src/app/Interfaces/interfaces';
import { AdminService } from 'src/app/services/admin.service';

@Component({
  selector: 'app-display-projects',
  templateUrl: './display-projects.component.html',
  styleUrls: ['./display-projects.component.css']
})
export class DisplayProjectsComponent implements OnInit {

  projectsArr:Project[]=[]

  constructor(private adminService:AdminService) { }

  ngOnInit(): void {
    this.getAllProjects()
  }

  getAllProjects(){
    this.adminService.displayProject().subscribe((response)=>{
      this.projectsArr=response


    })
  }
  deleteProj$ :Observable<any> = new Observable
  deleteProject(id:any){
    this.deleteProj$ =this.adminService.deleteProject(id).pipe(map)
    console.log(id)
    // setTimeout(() => {
    //   window.location.reload()
    // }, 500);
  }

}
<div >
    <div *ngIf="projectsArr.length;else noProjects" class="projectsContainer">
        <div *ngFor="let proj of projectsArr,let i=index" class="aProject">
            <p>{{proj.project_name}}</p>
            <p>{{proj.project_description}}</p>
            <p>{{proj.due_date}}</p> 
            <button (click)="deleteProject(proj.project_id)" class="deleteBtn">DELETE</button>
            {{deleteProj$ | async}}
        </div>
    </div>

</div>

<ng-template #noProjects>
    <p>No projects available!!</p>
</ng-template>
